void Map::draw(sf::RenderWindow& window) 
{
    int x,y;
    for (y = 0; y < grid.size(); y++)
        for (x = 0; x < grid[y].size(); x++)
        {
            std::string type = getTileType(y, x);
            tile.setTexture(&tileTextures[type]);
            tile.setPosition(x * TILE_SIZE, y * TILE_SIZE);
            window.draw(tile);
        }
}

bool Map::isWallAt(int x, int y) const
{
    int tileX = x / TILE_SIZE;
    int tileY = y / TILE_SIZE;

    if (tileY < 0 || tileY >= TILE_ROWS || tileX < 0 || tileX >= TILE_COLS)
        return false;
        
    return grid[tileY][tileX] == 1 || grid[tileY][tileX] == 2;
}

std::string Map::getTileType(int row, int col) const
{
    if (grid[row][col] == -1)
        return "void";

    if (grid[row][col] == 0)
        return "floor";

    if (grid[row][col] == 1)
        return "wall_x";

    if (grid[row][col] == 3)
        return "shadow1";
    
    if (grid[row][col] == 4)
        return "shadow2";

    if (grid[row][col] == 5)
        return "shadow3";

    if (grid[row][col] == 2)
        return "wall_y";


    return "void";
}
